mapper.select:
SELECT EXISTS
           (SELECT 1
            FROM product_review
            WHERE product_id = #{productId}
              AND user_id = #{userId});

SELECT *
FROM product_review

    product_id = #{productId}

        AND positive = #{positive}

mapper.delete:
DELETE
FROM product_review
WHERE product_id = #{productId}
  AND user_id = #{userId}

mapper.update:
UPDATE product_review
SET positive   = #{positive},
    created_at = NOW()
WHERE product_id = #{productId}
  AND user_id = #{userId}
