mapper.select:
SELECT EXISTS
           (SELECT 1
            FROM product_review
            WHERE product_id = #{productId}
              AND user_id = #{userId});

SELECT *
FROM product_review
WHERE product_id = #{productId}
AND user_id = #{userId}

SELECT *
FROM product_review
WHERE user_id = #{userId}
LIMIT #{size}
OFFSET #{size} * (#{page} - 1)

SELECT *
FROM product_review

    product_id = #{productId}

        AND is_liked = #{isLiked}


LIMIT #{size}
OFFSET #{size} * (#{page} - 1)

mapper.delete:
DELETE
FROM product_review
WHERE product_id = #{productId}
  AND user_id = #{userId}

mapper.update:
UPDATE product_review
SET is_liked   = #{isLiked},
    created_at = NOW()
WHERE product_id = #{productId}
  AND user_id = #{userId}
