mapper.select:
SELECT EXISTS
           (SELECT 1
            FROM product_review
            WHERE product_id = #{productId}
              AND user_id = #{userId});

SELECT *
FROM product_review
WHERE product_id = #{productId}
AND user_id = #{userId}

SELECT *
FROM product_review


        AND user_id = #{userId}


        AND product_id = #{productId}


        AND is_liked = #{isLiked}


LIMIT #{size}
OFFSET #{size} * (#{page} - 1)

SELECT COUNT(*)
FROM product_review


        AND user_id = #{userId}


        AND product_id = #{productId}


        AND is_liked = #{isLiked}

mapper.delete:
DELETE
FROM product_review
WHERE product_id = #{productId}
  AND user_id = #{userId}

mapper.update:
UPDATE product_review
SET is_liked   = #{isLiked},
    created_at = NOW()
WHERE product_id = #{productId}
  AND user_id = #{userId}
