mapper.select:
SELECT *
FROM "user"
ORDER BY id
    LIMIT #{size}
OFFSET (#{page} - 1) * #{size}

mapper.insert:
INSERT INTO user_role (user_id, role_id)
VALUES (#{id},
    (
        SELECT id
        FROM role
        WHERE name = #{role}
    )
)

mapper.delete:
DELETE
FROM user_role
WHERE user_id = #{id}
AND role_id =
        (
            SELECT id
            FROM role
            WHERE name = #{role}
        )
